        public UInt32 CalcCRC32(byte[] pBuf, UInt32 seed, UInt16 length)
            {
            UInt32 crc;
            UInt16 i;

            crc = ~seed;

            for (i = 0; i < length; i++)
                crc = ((crc >> 8) & 0x00FFFFFF) ^ Crc32Table[(crc ^ pBuf[i]) & 0xFF];

            return (crc ^ 0xFFFFFFFF);
            }


        public byte[] CalcCRC32(byte[] pBuf, UInt16 length)
            {
            UInt32 CRCResult = CalcCRC32(pBuf, 3802665723, length);
            return BitConverter.GetBytes(CRCResult);
            }


